const ItemModal = ({show, handleClose, children, description, onChangeDescription}) => {

  let [thisDescription, changeDescription] = useState(description);
  let [toInput, setToInput] = useState(false);
  const showHideClassName = show?"modal display-block" : "modal display-none";
  console.log(toInput);
  const Tag = toInput ? 
      <input className="alert alert-outline-secondary border-0 p-0 pl-1 col-8" value={thisDescription} placeholder={"input ..."} onChange={e => changeDescription(e.target.value)}></input>
      : <p className="col-12" onClick={setToInput(true)} placeholder='paragraph'> {thisDescription} </p>;
  return (
    <div className={showHideClassName}>
      <div className="col-6 offset-3 alert alert-light d-flex justify-content-center pl-5 pr-5">
        <section className="col-12">
          <h3 className="text-center">{children}</h3>
          <hr></hr>
            <div className="row ml-3">
              <small className="col-12">Description: </small>
              {/* <Content show={show} thisDescription={thisDescription} changeDescription={changeDescription} setChange={setChange}/> */}
              {/* <p className="col-12" onClick={()=>setChange(true)}>{thisDescription}</p> */}
              {Tag}
            </div>
          <hr></hr>
          <button type="button" className="btn btn-outline-danger justify-content-right" onClick={handleClose}>
            close window
          </button>
        </section>
      </div>
    </div>
  );
};




