import React,{Component} from 'react'

export default class TodoItem extends Component {
    constructor(props) {
        super(props);
        this.state = {
            done: false
        }
    }

    isDone = () => {
        this.setState(({done})=>({
            done:!done
        }))  
    }

    render() {

        let style = this.state.done?{textDecorationLine: 'line-through'}:{};
        let {title} = this.props;
        return(
            <div style ={style} className="alert alert-secondary" onClick={this.change}>
                 <input type="checkbox" className="form-check-input" checked={this.state.done} onChange={this.isDone}/>
                 {title}
                 <i class="text-right fa fa-minus-circle"></i>
            </div>
        )
    }
}
