import React,{Component} from 'react'
import TodoList from './todo-list'

export default class TodoCard extends Component {
    constructor(props) {
        super(props);
        this.state = [
            {title:'Learn Js', done:false, id:'alkdj'},
            {title:'Repeat react tutorial', done:false, id:'kdks'},
            {title:'Js documentation', done:false, id:'lakdi'},
        ];

        this.addTodoItem = this.addTodoItem.bind(this);
      }

      addTodoItem(title){
        const newTodo = {
          title,
          done:false,
          id: Date.now()
        };
        this.setState([...this.state, newTodo]);
      }

      updateTodo=(programming) => {
        this.setState(programming)
      }

    render() {
        return (
            <div className="card">
                <p>{props.title}</p>
                <TodoList addTodoItem = {this.addTodoItem} todos = {this.state} updateTodosHandle={this.updateTodo}></TodoList>
            </div>
        )
    }
}
