import React,{useState} from 'react'
import TodoList from './todo-list'

const TodoCard = ({cardTitle, key, deleteCard, addTodoItem, deleteTodosItem, changeTitleOfCard, todoitems, updateTodosHandle}) => {

  const [tagTextarea, setTagTextarea] = useState(false)
  const todoTitleTag = tagTextarea ? 
    <small className="form-control">
      <textarea rows={1} className=" d-inline text-center col-10 p-0 mb-2" value={cardTitle} 
      onChange={changeTitleOfCard} title="Change card title" style={{resize:'none', background:'white'}} onBlur={()=>setTagTextarea(false)} autoFocus></textarea>
    </small>
    : <p className="d-inline-block col-10 text-center" onClick={()=>setTagTextarea(true)}> {cardTitle || '...'} </p>;


  return (
      <div className="mr-2 pr-1 pl-1 col-2 d-inline-block" style={{background:'#f5f5f5'}}>
        <small className="alert border-0 mb-0 pb-0">
          {todoTitleTag}
          <i className="fa fa-minus-square col-2" title="Delete card" onClick={deleteCard}></i>
        </small>
        <div className="alert pt-3 pb-1">
          <TodoList  addTodoItem = {(title)=>addTodoItem(title)} key={key}
          todoitems = {todoitems} updateTodosHandle={updateTodosHandle} deleteTodosItem = {(itemIndex)=>deleteTodosItem(itemIndex)}></TodoList>
        </div>
      </div>
  )

}
export default TodoCard;
