import React,{Component} from 'react'
import {Button, Modal} from 'reactstrap'
import itemModal from './itemModal'

export default class TodoItem extends Component {
    constructor(props) {
        super(props);
        this.state = {
            done: false
        }
    }

    isDone = () => {
        this.setState(({done})=>({
            done:!done
        }))  
    }

    render() {
        const [modalShow, setModalShow] = React.useState(false);
        let style = this.state.done?{textDecorationLine: 'line-through'}:{};
        let {title} = this.props;
        return(
            // <div style ={style} className="alert alert-secondary row">
                 <>
                    <div className="col-10"  style ={style}>
                        <input type="checkbox" className="form-check-input" checked={this.state.done} onClick={this.isDone}/>
                        <small onClick={this.setModalShow(true)} style={{cursor:"zoom-in"}}>
                            {title.length > 20 ? title.substr(0, 20)+'...' : title}
                        </small>
                    </div>
                    <MyVerticallyCenteredModal
                    show={modalShow}
                    onHide={() => setModalShow(false)}
                    />
                 </>
            //      <i class="text-right fa fa-minus-circle col-2" onClick={this.isDone}/>
            // </div>
        )
    }
}
