import React,{Component} from 'react'
import TodoCard from './todo/todo-card'

class App extends Component{
  constructor() {
    super();
    this.state = [
      {title:'Read book'},
      {title:'Products'},
    ];
  }

  updateState(state) {
    this.setState(state);
  }

  addCard(setTitle) {
    const newCard = { title:setTitle}
    this.setState(...this.state,newCard)
  }

  render() {
    return (
      <div className="col-12 alert-warning">
        <TodoCard title="tester"/>
        {this.state.map(item => {
          return (
            <TodoCard title={item.title}></TodoCard>
          )
        })}

      </div>
    );
  }
}

export default App;
