                className="alert alert-outline-secondary border-0 p-0 pl-1 col-8"
                value={thisDescription} placeholder={'About ...'}
                onChange={e => changeDescription(e.target.value)}
                onClick={()=>setChange(!show)}
              >
                {!change?thisDescription:''}
              </Tag>
            </div>
          <hr></hr>
          <button type="button" className="btn btn-outline-danger justify-content-right" onClick={handleClose}>
            close window
          </button>
        </section>
      </div>
    </div>
  );
};




