import React,{Component} from 'react'
import { ReactSortable } from 'react-sortablejs';
import TodoCard from './todo/todo-card'

class App extends Component{
  constructor() {
    super();
    this.state = {

      card:
        [
          {title:'Read book', id:Date.now()},
          {title:'Products', id:Date.now()+1}
        ]
    };
  }

  addCard = (setTitle) => {
    const newCard = { title:'New card', id:Date.now(), index:this.i++}
    this.setState({card : [...this.state.card,newCard]})
  }

  changeTitleOfCard = (index, event) => {
    const newState = {...this.state};
    newState.card[index].title= event.target.value;
    this.setState(newState);
  }
  deleteCard = (index) => {
    this.setState(this.state.card.splice(index,1))
  }
  updateCards = (card) => {
    this.setState({card});
  }

  render() {
    return (
      <div className="col-12 alert-warning row">

      <ReactSortable list={this.state.card} setList={this.updateCards}>{
        this.state.card.map((item, index) => {
          return (
            <div className="col-3">
              <TodoCard cardTitle={item.title} changeTitleOfCard={(event)=>this.changeTitleOfCard(index, event)} deleteCard = {()=>this.deleteCard(index)}></TodoCard>
            </div>  
          )
        })
      }</ReactSortable>

          <i className="btn__add-card fa fa-plus-circle" onClick={this.addCard}/>
      </div>
    );
  }
}

export default App;
