import React,{Component} from 'react'

export default class TodoItem extends Component {
    constructor(props) {
        super(props);
        this.state = {
            done: false
        }
    }
    change = () => {
        this.setState(({done})=>({
            done:!done
        }))
        if(this.state.done){
            styles = {
                textDecorationLine: 'line-through'
            }
        } else { styles = {}}

    }


    render() {
        let {title} = this.props;
        return(
            <div style ={styles}  onClick={this.change}> <input type="checkbox" checked={this.state.done} onChange={this.change}/>{title}</div>
        )
    }
}
