import React from "react";
import { ReactSortable } from "react-sortablejs";
import TodoItem from './todo-item'

const TodoList  = (props) => {

    sortableList=()=>{
        for(let element in props) {
            <ReactSortable list={element} setList={props.updateTodosHandle}>{
                element.map(elem => {
                    const{id, ...el} = elem;
                    return(
                        <li style ={{listStyleType:"none"}} key={id} className="pl-5">
                            <TodoItem {...el}></TodoItem>
                        </li>
                    )
                })
            }</ReactSortable>  
        }
    }

    return(
        <div className="alert alert-warning pl-3">

            <ReactSortable list={props.todos.programming} setList={props.updateTodosHandle}>{
                props.todos.programming.map(element => {
                    const {id, ...elem} = element;
                    return(
                        <li style ={{listStyleType:"none"}} key={id} className="pl-5">
                            <TodoItem {...elem}></TodoItem>
                        </li>
                    )
                })
            }</ReactSortable>
        </div>
    )
    /* return (
            <ul className="alert alert-warning pl-3">
                {todos}
            </ul>
    ) */
}
export default TodoList;
