import React,{Component} from 'react'

export default class TodoItem extends Component {
    constructor(props) {
        super(props);
        this.state = {
            done: false
        }
    }
    change = () => {
        this.setState(({done})=>({
            done:!done
        }))
        if(this.state.done){
            console.log('yes');
        } else {
            console.log('no');
        }
    }


    render() {
        let {title} = this.props;
        return(
            <li onClick={this.change} > <input type="checkbox" checked={this.state.done}/>{title}</li>
        )
    }
}
