import React from "react";
import { ReactSortable } from "react-sortablejs";
import TodoItem from './todo-item'
import TodoInput from './todo-input'

const TodoList  = (props) => {

    return(
            <div className="col-3">
                <div className="alert alert-primary text-center">{props.title}</div>
                <ReactSortable list={props.todos} setList={props.updateTodosHandle}>{
                    props.todos.map(element => {
                        const {id, ...elem} = element;
                        return(
                            <li style ={{listStyleType:"none"}} key={id} className="pl-1 pr-1">
                                <TodoItem {...elem}></TodoItem>
                            </li>
                        )
                    })
                }</ReactSortable>
                <TodoInput add = {props.addTodoItem}></TodoInput>
            </div>
    )
}
export default TodoList;
