import React,{Component, useState} from 'react';
import {App} from './itemModal'
import {Modal, Button} from 'reactstrap'


export default class TodoItem extends Component {
    constructor(props) {
        super(props);
        this.state = {
            done: false,
            modalShow:false
        }
    }
    setModalShow = () => {
        this.setState(({modalShow}) => ({
            modalShow:!modalShow
        }))
    }
    isDone = () => {
        this.setState(({done})=>({
            done:!done
        }))  
    }

    modalShow = () => {
        console.log('heeeeeeeeee');
    }

    render() {
        const {modalShow} = this.state;
        let style = this.state.done?{textDecorationLine: 'line-through'}:{};
        let {title} = this.props;

        return(
                 <>
                    <div className="col-10"  style ={style}>
                        <input type="checkbox" className="form-check-input" checked={this.state.done} onChange={this.isDone}/>
                        <small  
                            variant="primary"
                            style={{cursor:"zoom-in"}}
                            onClick={this.modalShow}>
                                {title.length > 20 ? title.substr(0, 20)+'...' : title}
                        </small>
                        <button type="button" class="btn btn-primary" data-toggle="modal" data-target="#exampleModal">
  Launch demo modal
</button>

<div class="modal fade" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div class="modal-dialog" role="document">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="exampleModalLabel">Modal title</h5>
        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div class="modal-body">
        ...
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
        <button type="button" class="btn btn-primary">Save changes</button>
      </div>
    </div>
  </div>
</div>
                    </div>

                 </>
        )
    }
}
