import React,{Component, useState} from 'react';
import {App} from './itemModal'
import Modal from 'react-bootstrap/Modal'


export default class TodoItem extends Component {
    constructor(props) {
        super(props);
        this.state = {
            done: false,
            modalShow:false
        }
    }
    setModalShow = () => {
        this.setState(({modalShow}) => ({
            modalShow:!modalShow
        }))
    }
    isDone = () => {
        this.setState(({done})=>({
            done:!done
        }))  
    }

    render() {
        const {modalShow} = this.state;
        let style = this.state.done?{textDecorationLine: 'line-through'}:{};
        let {title} = this.props;

        return(
                 <>
                    <div className="col-10"  style ={style}>
                        <input type="checkbox" className="form-check-input" checked={this.state.done} onChange={this.isDone}/>
                        <small  
                            variant="primary"
                            style={{cursor:"zoom-in"}}
                            title={title.length > 20 ? title.substr(0, 20)+'...' : title}>
                        </small>
                    </div>

                        <Example></Example>
                 </>
        )
    }
}

function Example() {
    const [lgShow, setLgShow] = useState(false);

    return (
      <>
        <Button onClick={() => setLgShow(true)}>Large modal</Button>
        <Modal
          size="lg"
          show={lgShow}
          onHide={() => setLgShow(false)}
          aria-labelledby="example-modal-sizes-title-lg"
        >
          <Modal.Header closeButton>
            <Modal.Title id="example-modal-sizes-title-lg">
              Large Modal
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>...</Modal.Body>
        </Modal>
      </>
    );
  }
