import React,{Component} from 'react'
import TodoCard from './todo/todo-card'

class App extends Component{
  constructor() {
    super();
    this.state = {
      card:
        [
          {title:'Read book', id:Date.now()},
          {title:'Products', id:Date.now()+1}
        ]
    };
  }

  addCard = (setTitle) => {
    const newCard = { title:'New card', id:Date.now()}
    this.setState({card : [...this.state.card,newCard]})
  }

  changeTitleofcard = (e) => {
    this.setState({
      title: e.target.value
    })
  }

  render() {
    return (
      <div className="col-12 alert-warning row">
        {this.state.card.map(item => {
          return (
            <TodoCard cardTitle={item.title} changeTitleofcard={this.changeTitleofcard} key = {item.id} ></TodoCard>
          )
        })}
          <i class="btn__add-card fa fa-plus-circle" onClick={this.addCard}/>
      </div>
    );
  }
}

export default App;
