import React,{Component} from 'react'

export default class TodoItem extends Component {
    constructor(props) {
        super(props);
        this.state = {
            done: false
        }
    }

    change = () => {
        this.setState(({done})=>({
            done:!done
        }))  
    }

    render() {
        let style = this.done?{textDecorationLine: line-through}:{};
        let {title} = this.props;

        return(
            <div style ={style}  onClick={this.change}> <input type="checkbox" checked={this.state.done} onChange={this.change}/>{title}</div>
        )
    }
}
