import React,{Component} from 'react'

export default class TodoItem extends Component {
    constructor(props) {
        super(props);
        this.state = {
            done: false
        }
    }

    isDone = () => {
        this.setState(({done})=>({
            done:!done
        }))  
    }
    itemModal = (e) => {
        return (
            <Modal
              {...props}
              size="lg"
              aria-labelledby="contained-modal-title-vcenter"
              centered
            >
              <Modal.Header closeButton>
                <Modal.Title id="contained-modal-title-vcenter">
                  Modal heading
                </Modal.Title>
              </Modal.Header>
              <Modal.Body>
                <h4>Centered Modal</h4>
                <p>
                  Cras mattis consectetur purus sit amet fermentum. Cras justo odio,
                  dapibus ac facilisis in, egestas eget quam. Morbi leo risus, porta ac
                  consectetur ac, vestibulum at eros.
                </p>
              </Modal.Body>
              <Modal.Footer>
                <Button onClick={props.onHide}>Close</Button>
              </Modal.Footer>
            </Modal>
          );
    }
    render() {
        let style = this.state.done?{textDecorationLine: 'line-through'}:{};
        let {title} = this.props;
        return(
            // <div style ={style} className="alert alert-secondary row">
                 <div className="col-10"  style ={style}>
                    <input type="checkbox" className="form-check-input" checked={this.state.done} onClick={this.isDone}/>
                    <small onClick={this.itemModal} style={{cursor:"zoom-in"}}>
                        {title.length > 20 ? title.substr(0, 20)+'...' : title}
                    </small>
                 </div>
            //      <i class="text-right fa fa-minus-circle col-2" onClick={this.isDone}/>
            // </div>
        )
    }
}
