import React,{useState} from 'react';
import './item-modal.css';


const TodoActions = ({action, deleteTodosItem, color, onChangeColor}) => {

    const [selectColor, setSelectColor] = useState(false);



    return (
        <div className={action?'m-0 p-0':'d-none'} style={{position:'absolute', left:'100%', top:'-10%', zIndex:'10'}}>
            <ul className="shadow-lg text-dark d-flex flex-column m-0 p-0">
                <li className="rounded text-light fa fa-trash p-1 cursor-pointer todo__item-action small" onClick={deleteTodosItem}> delete todo
                <li className="rounded text-light fa fa-paint-brush p-1 cursor-pointer todo__item-action small"
                onClick={()=>setSelectColor(!selectColor)}>Color</li>
                {selectColor? 
                    <li>
                        <input type="checkbox" className="bg-danger"/>
                        <input type="checkbox" className="bg-primary"/>
                        <input type="checkbox" className="bg-warning"/>
                    </li>

                    :''}
                 </li>
                <li className="rounded text-light fa fa-trash p-1 cursor-pointer todo__item-action small" onClick={deleteTodosItem}> delete todo kanday</li>
            </ul>
        </div>
    )
}
export default TodoActions;
