import React from 'react';

const TodoActions = ({action, deleteTodosItem, onChangeColor}) => {


    return (
        <div className={action?'m-0 p-0 bg-info':'d-none'} style={{position:'absolute', left:'100%', top:'-10%', zIndex:'10'}} 
            >
            <ul className="shadow-lg text-dark  m-0 p-0">
                <li className="alert text-light fa fa-trash p-1 m-1 cursor-pointer d-block todo__item-action small" onClick={deleteTodosItem}> delete todo </li>
                <li className="alert text-light fa fa-paint-brush p-1 m-1 cursor-pointer d-block todo__item-action small"
                onClick={onChangeColor}>Color</li>
                <li className="alert text-light fa fa-trash p-1 m-1 cursor-pointer d-block todo__item-action small" onClick={deleteTodosItem}> delete todo </li>

            </ul>
        </div>
    )
}
export default TodoActions;
