import React,{Component} from 'react'
import TodoList from './todo-list'

export default class TodoCard extends Component {
    constructor(props) {
        super(props);
        this.state = {
            todos: [
                {title:'Learn Js', done:false, id:'alkdj'},
                {title:'Repeat react tutorial', done:false, id:'kdks'},
                {title:'Js documentation', done:false, id:'lakdi'},
            ]
        };

        this.addTodoItem = this.addTodoItem.bind(this);
      }

      addTodoItem(title){
        const newTodo = {
          title,
          done:false,
          id: Date.now()
        };
        this.setState([...this.state, newTodo]);
      }

      updateTodo=(programming) => {
        this.setState({todos: programming})
      }

    render() {        
        return (
            <div className="card">                
                <TodoList title={this.props.title} addTodoItem = {this.addTodoItem} 
                todos = {this.state.todos} updateTodosHandle={this.updateTodo}></TodoList>
                {this.props.card.map(item => {
                    return(
                        <TodoList title={item.title} addTodoItem = {this.addTodoItem} 
                        todos = {this.state.todos} updateTodosHandle={this.updateTodo}></TodoList>
                    )
                })}
            </div>
        )
    }
}
