import React,{Component, useState} from 'react';
import ItemModal from './itemModal'

export default class TodoItem extends Component {
    constructor(props) {
        super(props);
        this.state = {
            done: false,
            modalShow:false
        }
    }
    setModalShow = () => {
        this.setState(({modalShow}) => ({
            modalShow:!modalShow
        }))
    }
    isDone = () => {
        this.setState(({done})=>({
            done:!done
        }))  
    }

    render() {
        const {modalShow} = this.state;
        let style = this.state.done?{textDecorationLine: 'line-through'}:{};
        let {title} = this.props;


        return(
                 <>
                    <div className="col-10"  style ={style}>
                        <input type="checkbox" className="form-check-input" checked={this.state.done} onChange={this.isDone}/>
                        <small variant="primary" onClick={this.setModalShow} style={{cursor:"zoom-in"}}>
                            {title.length > 20 ? title.substr(0, 20)+'...' : title}
                        </small>
                    </div>
                    <ItemModal
                    show={modalShow}
                    onHide={this.setModalShow}
                    >

                    </ItemModal>
                 </>
        )
    }
}
