import React, {useState} from 'react';
import './item-modal.css'
import '../../node_modules/bootstrap/dist/css/bootstrap.css';
import '../../node_modules/bootstrap/dist/css/bootstrap.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCoffee } from '@fortawesome/free-solid-svg-icons'

const ItemModal = ({show, handleClose, children, description, onChangeDescription}) => {

  let [thisDescription, changeDescription] = useState(description);
  let [toInputDescription, setToInputDescription] = useState(false);

  const showHideClassName = show?"modal display-block" : "modal display-none";

  const descriptionTag = toInputDescription ? 
      <textarea className="alert alert-outline-secondary border-0 p-0 pl-1 col-8 ml-3" 
      onBlur={()=> setToInputDescription(false)} onChange={e => changeDescription(e.target.value)}
      rows={5} autoFocus>
        {thisDescription}
      </textarea>
      : <p className="col-12" onClick={()=>setToInputDescription(true)}> {thisDescription || '...'} </p>;

  const todoTitleTag = toInputDescription ? 
      <textarea className="alert alert-outline-secondary border-0 p-0 pl-1 col-8 ml-3" 
      onBlur={()=> setToInputDescription(false)} onChange={e => changeDescription(e.target.value)}
      rows={5} autoFocus>
        {description}
      </textarea>
      : <p className="col-12" onClick={()=>setToInputDescription(true)}> {description || '...'} </p>;




  return (
    <div className={showHideClassName}>
      <div className="col-6 offset-3 alert alert-light d-flex justify-content-center pl-5 pr-5">
        <section className="col-12">
          <h3 className="text-center">{children}</h3>
          <hr></hr>
            <div className="row">
              <small className="col-12">Description: </small>
              {descriptionTag}
            </div>
          <hr></hr>
          <button type="button" className="btn btn-outline-danger justify-content-right" onClick={handleClose}>
            close window
          </button>
        </section>
      </div>
    </div>
  );
};




export {ItemModal};
