import React from 'react'
import TodoItem from './todo-item';
import {ReactSortable} from 'react-sortablejs';

interface ItemType {
    id: string;
    title: string;
    done:Boolean;
  }

const TodoList = ({props}, setState) => {
    const todos =
        <ReactSortable list={props} setList={setState}>{
            props.map(element => {
                const {id, ...elem} = element;
                return(
                    <li style ={{listStyleType:"none"}}key={id} className="pl-5">
                        <TodoItem {...elem}></TodoItem>
                    </li>
                )
            })
        }</ReactSortable>
    return (
            <ul className="alert alert-warning pl-3">
                {todos}
            </ul>
    )
}
export default  TodoList;
