import React, {useState} from 'react';

const Todos = () => {
    const [todos, setTodos] = useState(
        [
            {title:'todo1'},
            {title:'todo2'},
            {title:'todo3'}
        ]
        )

    return (
        <>
            {
                todos.map((item) => {
                    return (
                        <div className="alert alert-info">{item.title}</div>
                    )
                })
            }
            <button className="btn btn-success" onClick={setTodos(...todos,{title:'newTodo'})}> +add desc</button>
        </>
    )
}

export default Todos;
