import React,{Component} from 'react'
import { ReactSortable } from 'react-sortablejs';
import TodoCard from './todo/todo-card'

class App extends Component{
  constructor() {
    super();
    this.state = {

      card:
        [
          {
            title:'Read book',
            id:Date.now(),
            todoitems: []
          },
          {
            title:'Products',
            id:Date.now(),
            todoitems: []
          }
        ]
    };

    this.addTodoItem = this.addTodoItem.bind(this);
  }

  //card functions

  addCard = (setTitle) => {
    const newCard = { title:'New card', id:Date.now(), index:this.i++}
    this.setState({card : [...this.state.card,newCard]})
  }

  changeTitleOfCard = (index, event) => {
    const newState = {...this.state};
    newState.card[index].title= event.target.value;
    this.setState(newState);
  }
  deleteCard = (index) => {
    this.setState(this.state.card.splice(index,1))
  }
  updateCards = (card) => {
    this.setState({card});
  }

  //Todo items functions

  addTodoItem(title) {
    if(title.trim()) {
      const newTodo = {
        title:title,
        done:false,
        id: Date.now(),
        chosen:false,
        selected: false
      };
      this.setState({todoitems : [...this.state.card[1].todoitems, newTodo]});
      console.log(this.state);
    }
  }

  render() {
    return (
      <div className="col-12 alert-warning row">
{
        this.state.card.map((item, index) => {
          return (
              <TodoCard cardTitle={item.title} changeTitleOfCard={(event)=>this.changeTitleOfCard(index, event)} deleteCard = {()=>this.deleteCard(index)}
              addTodoItem={this.addTodoItem(index)}></TodoCard>
          )
        })
      }
          <i className="btn__add-card fa fa-plus-circle" onClick={this.addCard}/>
      </div>
    );
  }
}

export default App;
