import React,{Component} from 'react'

export default class TodoItem extends Component {
    constructor(props) {
        super(props);
        this.state = {
            done: false
        }
    }

    change = () => {
        this.setState(({done})=>({
            done:!done
        }))  
    }

    render() {
        let style = this.state.done?{textDecorationLine: 'line-through'}:{};
        let {title} = this.props;

        return(
            <div style ={style} className="alert alert-secondary text-center" onClick={this.change}>
                 <input type="checkbox" className="form-check-input" checked={this.state.done} onChange={this.change}/>
                 {title}
            </div>
        )
    }
}
