import React,{Component} from 'react'
import TodoList from './todo/todo-list'
import TodoInput from './todo/todo-input'

class App extends Component{
  constructor() {
    super();
    this.state = {
      programming:[
          {title:'Learn Js', done:false, id:'alkdj'},
          {title:'Repeat react tutorial', done:false, id:'kdks'},
          {title:'Js documentation', done:false, id:'lakdi'},
        ]
    };

    this.addTodoItem = this.addTodoItem.bind(this);
    this.todos = this.todos.bind(this);
  }

  addTodoItem(title){
    const newTodo = {
      title,
      done:false,
      id: Date.now()
    };
    this.setState({programming:[...this.state.programming, newTodo]});
  }

  updateTodoList=(programming) => {
    this.setState({programming})
  }

  todos() {
      return (
         <TodoList todos={this.state.programming} updateTodosHandle={this.updateTodo}></TodoList>
      )

  }

  render() {
    return (
      <div className="App">
        { 
          this.todos
        }
        <TodoInput addTodo = {this.addTodoItem}></TodoInput>
      </div>
    );
  }
}

export default App;
