import React,{Component} from 'react'
import TodoList from './todo-list'

export default class TodoCard extends Component {
    constructor(props) {
        super(props);
        this.state = {
            todoitems: []

        };
        this.addTodoItem = this.addTodoItem.bind(this);
    }

    addTodoItem(title) {
      if(title.trim()) {
        const newTodo = {
          title:title,
          done:false,
          id: Date.now(),
          chosen:false,
          selected: false
        };
        this.setState({todoitems:[...this.state.todoitems, newTodo]});
      }
    }

    updateTodo = (todoitems) => {
      this.setState({todoitems});
    }

    render() {
        return (
            <div className="alert alert-info mr-3 pr-3 pl-3">
              <input className="alert alert-primary text-center" value={props.title} onChange={props.changeTitleOfCard}></input>
              <TodoList title={this.props.cardTitle} changeTitleOfCard={(event) => this.props.changeTitleOfCard(event)} addTodoItem = {this.addTodoItem}
              todoitems = {this.state.todoitems} updateTodosHandle={this.updateTodo}></TodoList>
            </div>
        )
    }
}
