import React from "react";
import { ReactSortable } from "react-sortablejs";
import TodoItem from './todo-item'

const TodoList  = (props) => {

    const sortableList = () => {

            for(let element in props) {
                return(
                    <div className="alert alert-info col-3">
                        <ReactSortable list={element} setList={props.updateTodosHandle}>{
                            element.map(elem => {
                                const{id, ...el} = elem;

                                    <li style ={{listStyleType:"none"}} key={id} className="pl-5">
                                        <TodoItem {...el}></TodoItem>
                                    </li>

                            })
                        }</ReactSortable>  
                    </div>
                )
            }

    }

    return(
       /*  <div className="alert alert-info col-12 row">
            <div className="alert alert-warning col-3">
                <ReactSortable list={props.todos.read} setList={props.updateTodosHandle}>{
                    props.todos.read.map(element => {
                        const {id, ...elem} = element;
                        return(
                            <li style ={{listStyleType:"none"}} key={id} className="pl-5">
                                <TodoItem {...elem}></TodoItem>
                            </li>
                        )
                    })
                }</ReactSortable>
            </div>
        </div> */
        {sortableList}
    )
    /* return (
            <ul className="alert alert-warning pl-3">
                {todos}
            </ul>
    ) */
}
export default TodoList;
