import React,{Component} from 'react'
import TodoList from './todo/todo-list'
import TodoInput from './todo/todo-input'

class App extends Component{
  constructor() {
    this.state = {
      todos:[
          {title:'Learn Js', done:false, id:'alkdj'},
          {title:'Repeat react tutorial', done:true, id:'kdks'},
          {title:'Js documentation', done:false, id:'lakdi'},
        ]
    }

    this.addTodo = this.addTodo.bind(this);
  }

  addTodo(title){
    const a = {
      title,
      done:false,
      id: Date.now()
    }
    this.setState({todos:[...this.state.todos, ...a]});
  }
  render() {
    return (
      <div className="App">
        <TodoList props={this.state.todos}></TodoList>
        <TodoInput addTodo = {this.addTodo}></TodoInput>
      </div>
    );
  }
}

export default App;
