import React,{Component} from 'react'

export default class TodoItem extends Component {
    constructor(props) {
        super(props);
        this.state = {
            done: false
        }
    }

    change = () => {
        this.setState(({done})=>({
            done:!done
        }))

    }
    style = () => {
        if(this.state.done){
               return "textDecorationLine: 'line-through'"

        } else { return ''}
    }


    render() {
        let {title} = this.props;
        return(
            <div style ={this.style}  onClick={this.change}> <input type="checkbox" checked={this.state.done} onChange={this.change}/>{title}</div>
        )
    }
}
