import React from 'react';
import './item-modal.css';


const TodoActions = ({action, deleteTodosItem, onChangeColor}) => {


    return (
        <div className="w-100 h-100 bg-secondary">
            <div className={action?'m-0 p-0':'d-none'} style={{ left:'100%', top:'-10%', zIndex:'10'}} 
                >
                <ul className="shadow-lg text-dark d-flex flex-column m-0 p-0">
                    <li className="rounded text-light fa fa-trash p-1 cursor-pointer todo__item-action small" onClick={deleteTodosItem}> delete todo </li>
                    <li className="rounded text-light fa fa-paint-brush p-1 cursor-pointer todo__item-action small"
                    onClick={onChangeColor}>Color</li>
                    <li className="rounded text-light fa fa-trash p-1 cursor-pointer todo__item-action small" onClick={deleteTodosItem}> delete todo kanday</li>

                </ul>
            </div>
        </div>
    )
}
export default TodoActions;
