import React,{Component} from 'react'
import TodoList from './todo-list'

export default class TodoCard extends Component {
    constructor(props) {
        super(props);
        /* this.state = {
            todoitems: []
        }; */
/*         this.addTodoItem = this.addTodoItem.bind(this);
 */    }

    /* addTodoItem(title) {
      if(title.trim()) {
        const newTodo = {
          title:title,
          done:false,
          id: Date.now(),
          chosen:false,
          selected: false
        };
        this.setState({todoitems:[...this.state.todoitems, newTodo]});
      }
    } */

   /*  updateTodosHandle = (todoitems) => {
      this.setState({todoitems});
    } */

    /* deleteTodosItem = (index) => {
      this.setState(this.state.todoitems.splice(index,1))
    } */


    render() {
        return (
            <div className="alert alert-info ml-3 mr-2 pr-3 pl-3 h-100">
              <div className="alert alert-primary border-0 pt-1 pb-0">
                <input className="form-control d-inline bg-light text-center col-10" value={this.props.cardTitle} onChange={this.props.changeTitleOfCard}
                title="Change card title"></input>
                <i className="fa fa-minus-square col-2" title="Delete card" onClick={this.props.deleteCard}></i>
              </div>
              <div className="alert alert-light pt-5 pb-1">
                <TodoList  addTodoItem = {(title)=>this.props.addTodoItem(title)} key={this.props.key}
                todoitems = {this.props.todoitems} updateTodosHandle={this.props.updateTodosHandle} deleteTodosItem = {(itemIndex)=>this.props.deleteTodosItem(itemIndex)}></TodoList>
              </div>
            </div>
        )
    }
}
