import React,{Component} from 'react'
import TodoList from './todo/todo-list'
import TodoInput from './todo/todo-input'

class App extends Component{
  constructor() {
    super();
    this.state = {
      todos:[
          {title:'Learn Js', done:false, id:'alkdj'},
          {title:'Repeat react tutorial', done:false, id:'kdks'},
          {title:'Js documentation', done:false, id:'lakdi'},
        ]
    };

    this.addTodo = this.addTodo.bind(this);
  }

  addTodoItem(title){
    const newTodo = {
      title,
      done:false,
      id: Date.now()
    };
    this.setState({todos:[...this.state.todos, newTodo]});
  }

  updateTodo=(todos) => {
    this.setState({todos})
  }

  render() {
    return (
      <div className="App">
        <TodoList todos={this.state.todos} updateTodosHandle={this.updateTodo}></TodoList>
        <TodoInput addTodo = {this.addTodoItem}></TodoInput>
      </div>
    );
  }
}

export default App;
