import React,{Component} from 'react'
import TodoList from './todo-list'

export default class TodoCard extends Component {
    constructor(props) {
        super(props);
        this.state = {
            todoitems: []

        };
        this.addTodoItem = this.addTodoItem.bind(this);
    }

    addTodoItem(title) {
      if(title.trim()) {
        const newTodo = {
          title:title,
          done:false,
          id: Date.now(),
          chosen:false,
          selected: false
        };
        this.setState({todoitems:[...this.state.todoitems, newTodo]});
      }
    }

    updateTodo = (todoitems) => {
      this.setState({todoitems});
    }

    render() {
        return (
            <div className="alert alert-info ml-3 mr-3 pr-3 pl-3">
              <input className="alert alert-primary text-center col-10" value={this.props.cardTitle} onChange={this.props.changeTitleOfCard}
              title="Change card title"></input>
              <i class="danger fa fa-minus-square col-2" title="Delete todo card" ></i>
              <div className="alert alert-light pt-5 pb-1 ">
              <TodoList  addTodoItem = {this.addTodoItem}
              todoitems = {this.state.todoitems} updateTodosHandle={this.updateTodo}></TodoList>
              </div>
            </div>
        )
    }
}
