import React, {useState} from 'react';

const Todos = () => {
    const [todos, setTodos] = useState(
        [
            {title:'todo1'},
            {title:'todo2'},
            {title:'todo3'}
        ]
        )
    return (
        <>
        <div className="alert">
        {
                    <div className="alert alert-info">{todos[1].title}</div>

        }

        </div>
            <button className="btn btn-success" onClick={setTodos((todos)=>[...todos,{title:'newTodo'}])}> +add desc</button>
        </>
    )
}

export default Todos;
