import React from 'react';
import './item-modal.css'
import '../../node_modules/bootstrap/dist/css/bootstrap.css';
import '../../node_modules/bootstrap/dist/css/bootstrap.css';


const ItemModal = ({show, handleClose, children}) => {
  const showHideClassName = show?"modal display-block" : "modal display-none";  
  return (
  <div className={showHideClassName}>
    <div className="col-6 offset-3 alert alert-light d-flex justify-content-center pl-5 pr-5">
      <section className="col-12">
        <h3 className="text-center">{children}</h3>
        <hr></hr>
          <div className="row text-center">
            <small>Description: </small>
            <form className="form-group col-12 row" onSubmit={console.log('heeeeee')}>
                <input  className="alert alert-outline-secondary border p-0 pl-1 col-10 outline-1 col-8"></input>
                <input type="submit" className="btn btn-outline-secondary border-0 h2" value="+" />
            </form>
          </div>
        <hr></hr>
        <button type="button" className="btn btn-secondary" onClick={handleClose}>
          close window
        </button>
      </section>
    </div>
   </div>
  );
};

export {ItemModal};
