import React,{Component} from 'react'

export default class TodoItem extends Component {
    constructor(props) {
        super(props);
        this.state = {
            done: false
        }
    }
    change = () => {
        this.setState(({done})=>({
            done:!done
        }))
    }


    render() {
        let input,{title} = this.props;
        const {done} = this.state;
        if(done){
            input ='<input type="checkbox" checked/>';
        }else {
            input = '<input type="checkbox"/>';
        }
        return(
            <li onClick={this.change} >{this.state.done?'checked':''}{title}</li>
        )
    }
}
