import React,{Component} from 'react'
import { ReactSortable } from 'react-sortablejs';
import TodoCard from './todo/todo-card'

class App extends Component{
  constructor() {
    this.state = {

      card:
        [
          {
            title:'Read book',
            id:Date.now(),
            todoitems: [
              {title:'first'},
              {title:'second'},
              {title:'thirthd'},
            ]
          },
          {
            title:'Products',
            id:Date.now(),
            todoitems: []
          }
        ]
    };
  }

  //card functions

  addCard = (setTitle) => {
    const newCard = { title:'New card', id:Date.now(), index:this.i++, todoitems:[]}
    this.setState({card : [...this.state.card,newCard]})
  }

  changeTitleOfCard = (index, event) => {
    const newState = {...this.state};
    newState.card[index].title= event.target.value;
    this.setState(newState);
  }

  deleteCard = (index) => {
    this.setState(this.state.card.splice(index,1))
  }

  updateCards = (card) => {
    this.setState({card});
  }

  //Todo items functions

  addTodoItem = (title, index) => {
    // console.log(...this.state.card[index].todoitems);
    if(title.trim()) {
      const newTodo = {
        title:title,
        done:false,
        id: Date.now(),
        chosen:false,
        selected: false
      };

      const card = this.copyTodoCard();
      card[index].todoitems.push(newTodo);
      this.setState({card});
      // console.log(this.state);
    }
  }

  copyTodoCard = () => {
   return this.state.card;
  }

  updateTodosHandle = (todoitems) => {
    this.setState(card => {
      card.todoitems = todoitems;
      return card
    });
  }

  deleteTodosItem = (itemIndex, index) => {
    this.setState(this.state.card[index].todoitems.splice(itemIndex,1));
  }

  render() {
    return (
      <div className="col-12 alert-warning row">
        {
          this.state.card.map((item, index) => {
            return (
                <TodoCard cardTitle={item.title} changeTitleOfCard={(event)=>this.changeTitleOfCard(index, event)} deleteCard = {()=>this.deleteCard(index)} key={this.state.card.id}
                addTodoItem={(title)=>this.addTodoItem(title,index)} todoitems = {this.state.card[index].todoitems} updateTodosHandle = {this.updateTodosHandle} deleteTodosItem={(itemIndex)=>this.deleteTodosItem(itemIndex, index)}></TodoCard>
            )
          })
        }
        <i className="btn__add-card fa fa-plus-circle" onClick={this.addCard}/>
      </div>
    );
  }
}

export default App;
