import React from "react";
import { ReactSortable } from "react-sortablejs";
import TodoItem from './todo-item'

const TodoList  = (props) => {

    const sortableList = () => {

            for(let element in props.todos) {
                <div className="alert alert-info col-3">
                    <ReactSortable list={element} setList={props.updateTodosHandle}>{
                        element.map(elem => {
                            const{id, ...el} = elem;
                            return(
                                <li style ={{listStyleType:"none"}} key={id} className="pl-5">
                                    <TodoItem {...el}></TodoItem>
                                </li>
                            )
                        })
                    }</ReactSortable>  
                </div>
            }

    }

    return(
        <div className="alert alert-info col-12 row">
            {sortableList}
        </div>
    )
    /* return (
