import React,{Component} from 'react'
import TodoList from './todo/todo-list'
import TodoCard from './todo/todo-card'

class App extends Component{
  constructor() {
    super();
    this.state = {
      programming:[
          {title:'Learn Js', done:false, id:'alkdj'},
          {title:'Repeat react tutorial', done:false, id:'kdks'},
          {title:'Js documentation', done:false, id:'lakdi'},
        ],
      read: [
        {title:'Квантовый воин',done:false, id:'lkdikd'},
        {title:'Перевод корана',done:false, id:'akdlslk'},
        {title:'Ас-сира',done:false, id:'aklkdiek'},
      ]
    };

    this.addTodoItem = this.addTodoItem.bind(this);
  }

  addTodoItem(title){
    const newTodo = {
      title,
      done:false,
      id: Date.now()
    };
    this.setState({programming:[...this.state.programming, newTodo]});
  }

  updateTodo=(programming) => {
    this.setState({programming})
  }

  todos = ()  => { 
    for(let obj in this.state) {
      return (
        <TodoList todos={obj} updateTodosHandle={this.updateTodo}></TodoList>
      )
    }
  }

  render() {
    return (
      <div className="col-12">
        <TodoCard title="tester"/>
      </div>
    );
  }
}

export default App;
