import React,{Component} from 'react';
import {ItemModal} from './itemModal'

export default class TodoItem extends Component {
    constructor(props) {
        super(props);
        this.state = {
            done: false,
            modalShow: false,
            color:'',
            action:false,
            isOpenActionItems:false
        }
    }
    setAction = () => {
        this.setState(({action}) => ({
            action:!action
        }))
    }
    toggleModal = () => {
        this.setState(({modalShow}) => ({
            modalShow:!modalShow
        }))
    }
    isDone = () => {
        this.setState(({done})=>({
            done:!done
        }))  
    }
    onChangeDescription = (description) => {
        this.setState(({description}))
    }
    onChangeColor = (color='yellow') => {
        this.setState(({color}));
    }

    isOpenActionItems = (e) => {
        if(this.state.action && !e) {
            console.log('yess');
        }
    }

    render() {

        const {modalShow, done, action} = this.state;   
        let style = done ? {textDecorationLine: 'line-through'} : {};
        let {title, index, deleteTodosItem} = this.props;
        const chevronClass = action ? 'fa-chevron-left' : 'fa-chevron-right';
        return(
            <>

                <div className="col-10"  style ={style}>
                    <input type="checkbox" className="form-check-input" checked={done} onChange={this.isDone}/>
                    <small  
                        variant="primary"
                        style={{cursor:"all-scroll"}}
                        onClick={this.toggleModal}>
                            {title.length > 20 ? title.substr(0, 20)+'...' : title}
                    </small>
                    <ItemModal
                        done = {done}
                        show={modalShow}
                        handleClose={this.toggleModal}
                        title={title}
                        todoItemIndex={index}>
                    </ItemModal>
                </div>
                <i className={`text-right fa ${chevronClass} col-2 pt-2 cursor-pointer`} onClick={()=>this.setAction()}
                    title="delete todo" style={{position:'relative', fontSize:'.55em'}}/>
                <div className={action?"w-100 h-100 bg-secondary":"d-none"} onClick={this.isOpenActionItems}>
                    <div className='m-0 p-0' style={{position:'absolute', left:'100%', top:'-10%', zIndex:'10'}} 
                        >
                        <ul className="shadow-lg text-dark  m-0 p-0">
                            <li className="alert text-light fa fa-trash p-1 m-1 cursor-pointer d-block todo__item-action small" onClick={deleteTodosItem}> delete todo </li>
                            <li className="alert text-light fa fa-paint-brush p-1 m-1 cursor-pointer d-block todo__item-action small"
                            onClick={() => this.onChangeColor()}>Color</li>
                            <li className="alert text-light fa fa-trash p-1 m-1 cursor-pointer d-block todo__item-action small" onClick={deleteTodosItem}> delete todo </li>

                        </ul>
                    </div>
                </div>
            </>
        )
    }
}
