import React, {useState} from 'react';
import Menu from '../inc/menu'
import LeftNav from '../inc/navLeft';
import './boards.css';

const Boards = () => {
    const [boards, setBoards] = useState(
        [
            {title:'todo1'},
            {title:'todo2'},
            {title:'todo3'}
        ]
        )
    return (
        <>
        <div className="boards__all-item">
        <Menu></Menu>
            {
                boards.map((item) => {

                return (
                    <div className="todos__item-container">
                        <h7>{item.title}</h7>
                    </div>
                )
                })

            }

            <button className="btn btn-success" onClick={()=>setBoards([...boards,{title:'newTodo'}])}> +add desc</button>
            </div>
        </>
    )
}

export default Boards;
