import React from "react";
import { ReactSortable } from "react-sortablejs";
import TodoItem from './todo-item'
import TodoInput from './todo-input'

const TodoList  = (props) => {


    return(
            <>

                <ReactSortable list={props.todoitems} setList={props.updateTodosHandle}>{
                    props.todoitems.map((element, index) => {
                        const {key, ...elem} = element;
                        return(
                            <div className="alert alert-light pt-1 pb-1 border-0 row text-dark shadow" style={{marginBottom:"0.3rem"}} key={index}>
                                <TodoItem {...elem} index={index}></TodoItem>
                                <i className="text-right fa fa-minus-circle col-2 pt-1 cursor-pointer" onClick={()=>props.deleteTodosItem(index)} title="delete todo"></i>
                                <i className="text-right fa fa-minus-circle col-2 pt-1 cursor-pointer" onClick={()=>props.deleteTodosItem(index)} title="delete todo"
                                    style={{position:'relative'}}/>
                                <ul style={{position:'absolute', left:'100%'}}>
                                    <li>first</li>
                                    <li>second</li>
                                    <li>thirth</li>
                                </ul>
                            </div>
                        )
                    })
                }</ReactSortable>
                <TodoInput addTodoItem = {props.addTodoItem}></TodoInput>
            </>
    )
}
export default TodoList;
