import React, {useState} from 'react';
import Menu from '../home/'
import './boards.css';

const Boards = () => {
    const [boards, setBoards] = useState(
        [
            {title:'todo1'},
            {title:'todo2'},
            {title:'todo3'}
        ]
        )
    return (
        <>
        <Menu></Menu>
            <div className="alert">
            {
                boards.map((item) => {

                return (
                    <div className="todos__item-container">
                        <h7>{item.title}</h7>
                    </div>
                )
                })

            }

            </div>
            <button className="btn btn-success" onClick={()=>setBoards([...boards,{title:'newTodo'}])}> +add desc</button>
        <LefNav></LefNav>
        </>
    )
}

export default Boards;
