import React, {Component} from 'react'
import { ReactSortable } from 'react-sortablejs';
import { AutoScroll } from 'sortablejs';
import TodoCard from './todo/todo-card'

class App extends Component{
  constructor(props) {
    super(props);
    this.state = {

      card:
        [
          {
            title:'Read book',
            id:Date.now(),
            todoitems: [
              {title:'Квантовый воин'},
              {title:'Будь лучшей версией себя!'},
              {title:'Бакыттын сырын ачтым'},
            ]
          },
          {
            title:'Products',
            id:Date.now(),
            todoitems: []
          }
        ]
    };
  }

  //card functions

  addCard = (setTitle) => {
    const newCard = { title:'New card', id:Date.now(), todoitems:[]}
    this.setState({card : [...this.state.card,newCard]})
  }

  changeTitleOfCard = (index, event) => {
    const newState = {...this.state};
    newState.card[index].title= event.target.value;
    this.setState(newState);
  }

  deleteCard = (index) => {
    this.setState(this.state.card.splice(index,1))
  }

  updateCards = (card) => {
    this.setState({card});
  }

  //Todo items functions

  addTodoItem = (title, index) => {
    // console.log(...this.state.card[index].todoitems);
    if(title.trim()) {
      const newTodo = {
        title:title,
        done:false,
        id: Date.now(),
        chosen:false,
        selected: false
      };

      const card = this.copyTodoCard();
      card[index].todoitems.push(newTodo);
      this.setState({card});
      console.log(this.state);
    }
  }

  copyTodoCard = () => {
   return this.state.card;
  }

  updateTodosHandle = (todoitems, index) => {
    const card = this.copyTodoCard();
    card[index].todoitems.splice(0, card[index].todoitems.length, ...todoitems);
    this.setState(card => {
      return card
    });

  }

  deleteTodosItem = (itemIndex, index) => {
    this.setState(this.state.card[index].todoitems.splice(itemIndex,1));
  }

  render() {
    return (
      <>
        {
          <ReactSortable list={this.state.card} setList={this.updateCards} className="row" style={{overflow:"scroll"}}>{  
            this.state.card.map((item, index) => {
              return (
                // <ReactSortable list={this.state.card} setList={this.updateCards}>{
                  <TodoCard cardTitle={item.title} changeTitleOfCard={(event)=>this.changeTitleOfCard(index, event)} deleteCard = {()=>this.deleteCard(index)} key={this.state.card.id}
                  addTodoItem={(title)=>this.addTodoItem(title,index)} todoitems = {this.state.card[index].todoitems} updateTodosHandle = {(todoitems) => this.updateTodosHandle(todoitems, index)} deleteTodosItem={(itemIndex)=>this.deleteTodosItem(itemIndex, index)}></TodoCard>
                // }</ReactSortable>
              )
            })
          }</ReactSortable>
        }
        <i className="btn__add-card fa fa-plus-circle" onClick={this.addCard}/>
      </>
    );
  }
}

export default App;
