import React,{Component} from 'react';
import {ItemModal} from './itemModal'

export default class TodoItem extends Component {
    constructor(props) {
        super(props);
        this.state = {
            description:'',
            done: false,
            modalShow:false
        }
    }
    toggleModal = () => {
        this.setState(({modalShow}) => ({
            modalShow:!modalShow
        }))
    }
    isDone = () => {
        this.setState(({done})=>({
            done:!done
        }))  
    }
    onChangeDescription = (event) => {
        this.setState({description: event.target.value})
        console.log(this.state);
    }

    render() {

        const {modalShow, done} = this.state;   
        let style = done ? {textDecorationLine: 'line-through'} : {};
        let {title} = this.props;
        return(
            <>

                <div className="col-10"  style ={style}>
                <ItemModal
                    show={modalShow}
                    handleClose={this.toggleModal}
                    description={this.state.description}
                    onChangeDescription= {(e) => this.onChangeDescription(e)}>
                    <p> {title} </p>
                </ItemModal>
                    <input type="checkbox" className="form-check-input" checked={this.state.done} onChange={this.isDone}/>
                    <small  
                        variant="primary"
                        style={{cursor:"zoom-in"}}
                        onClick={this.toggleModal}>
                            {title.length > 20 ? title.substr(0, 20)+'...' : title}
                    </small>
                </div>
            </>
        )
    }
}
