import React,{Component} from 'react'

export default class TodoItem extends Component {
    constructor(props) {
        super(props);
        this.state = {
            done: false
        }
    }

    isDone = () => {
        this.setState(({done})=>({
            done:!done
        }))  
    }

    render() {
        let style = this.state.done?{textDecorationLine: 'line-through'}:{};
        let {title} = this.props;
        return(
            // <div style ={style} className="alert alert-secondary row">
                 <span className="col-10"  style ={style} onClick={this.isDone}>
                    <input type="checkbox" className="form-check-input" checked={this.state.done}/>
                    {title.substr(0, 33)}
                 </span>
            //      <i class="text-right fa fa-minus-circle col-2" onClick={this.isDone}/>
            // </div>
        )
    }
}
