import React from 'react';
import './item-modal.css'
import '../../node_modules/bootstrap/dist/css/bootstrap.css';
import '../../node_modules/bootstrap/dist/css/bootstrap.css';


const ItemModal = ({show, handleClose, children}) => {
  const showHideClassName = show?"modal display-block" : "modal display-none";  
  return (
  <div className={showHideClassName} className='row'>
    <div className="col-6 offset-3 alert alert-light d-flex justify-content-center">
      <section className="">
        {children}
        <button type="btn btn-secondary" onClick={handleClose}>
          Close
        </button>
      </section>
    </div>
   </div>
  );
};

export {ItemModal};
