import React,{Component} from 'react';
import {ItemModal} from './itemModal'

export default class TodoItem extends Component {
    constructor(props) {
        super(props);
        this.state = {
            done: false,
            modalShow: false
        }
    }
    toggleModal = () => {
        this.setState(({modalShow}) => ({
            modalShow:!modalShow
        }))
    }
    isDone = () => {
        this.setState(({done})=>({
            done:!done
        }))  
    }
    onChangeDescription = (description) => {
        this.setState(({description}))
    }

    render() {

        const {modalShow, done} = this.state;   
        let style = done ? {textDecorationLine: 'line-through'} : {};
        let {title, index} = this.props;

        return(
            <>

                <div className="col-10"  style ={style}>
                <ItemModal
                    show={modalShow}
                    handleClose={this.toggleModal}
                    title={title}
                    todoItemIndex={index}>
                </ItemModal>
                    <input type="checkbox" className="form-check-input shadow" checked={this.state.done} onChange={this.isDone}/>
                    <small  
                        variant="primary"
                        style={{cursor:"zoom-in"}}
                        onClick={this.toggleModal}>
                            {title.length > 20 ? title.substr(0, 20)+'...' : title}
                    </small>
                </div>
            </>
        )
    }
}
